        [pstr appendString: [sensorPath valueForKey: kServiceString] ];
        [pstr appendString: @" , Option: " ];
        [pstr appendString: [sensorPath valueForKey: kSetupOptionString] ];
        return [pstr autorelease]; //mah 09/01/09 added autorelease
    }else{
        return @"";
    }
}

