#import "ORCP8CryopumpDecoders.h"
#import "ORDataPacket.h"
#import "ORDataSet.h"

//------------------------------------------------------------------------------------------------
// Data Format
// xxxx xxxx xxxx xxxx xxxx xxxx xxxx xxxx
// ^^^^ ^^^^ ^^^^ ^^------------------------data id
//                  ^^ ^^^^ ^^^^ ^^^^ ^^^^--length in longs
//
// xxxx xxxx xxxx xxxx xxxx xxxx xxxx xxxx
//                          ^^^^ ^^^^ ^^^^--device id
// xxxx xxxx xxxx xxxx xxxx xxxx xxxx xxxx  first stage temp encoded as a float
// xxxx xxxx xxxx xxxx xxxx xxxx xxxx xxxx  second stage temp encoded as a float
// xxxx xxxx xxxx xxxx xxxx xxxx xxxx xxxx  time temperature taken in seconds since Jan 1, 1970
//-----------------------------------------------------------------------------------------------
@implementation ORCP8CryopumpDecoderForTemperatures

- (unsigned long) decodeData:(void*)someData fromDecoder:(ORDecoder*)aDecoder intoDataSet:(ORDataSet*)aDataSet
{
	unsigned long *dataPtr = (unsigned long*)someData;
	union {
		float asFloat;
		unsigned long asLong;
	}theTemperature;
	int ident = dataPtr[1] & 0xfff;
	theTemperature.asLong = dataPtr[2];		//encoded as float, use union to convert
	[aDataSet loadTimeSeries:theTemperature.asFloat										
						  atTime:dataPtr[4]
						  sender:self 
						withKeys:@"CP8Cryopump",[NSString stringWithFormat:@"%d",ident],@"FirstStageTemp",
								nil];
	
	theTemperature.asLong = dataPtr[3];			//encoded as float, use union to convert
	[aDataSet loadTimeSeries:theTemperature.asFloat										
					  atTime:dataPtr[4]
					  sender:self 
					withKeys:@"CP8Cryopump",[NSString stringWithFormat:@"%d",ident],@"SecondStageTemp",
					nil];
	
	
	return ExtractLength(dataPtr[0]);
}

- (NSString*) dataRecordDescription:(unsigned long*)dataPtr
{
    NSString* title= @"CP8Cryopump Controller\n\n";
 	int ident = dataPtr[1] & 0xfff;
	NSString* theString =  [NSString stringWithFormat:@"%@ %d\n",title,ident];               
	union {
		float asFloat;
		unsigned long asLong;
	}theData;
	
    NSDate* date = [NSDate dateWithTimeIntervalSince1970:(NSTimeInterval)dataPtr[4]];
		
    theData.asLong = dataPtr[2];
    theString = [theString stringByAppendingFormat:@"First Stage Temp: %.1f %@\n",theData.asFloat,[date stdDescription]];
	
	theData.asLong = dataPtr[3];
	theString = [theString stringByAppendingFormat:@"Second Stage Temp: %.1f %@\n",theData.asFloat,[date stdDescription]];
	return theString;
}
@end


