}

- (void) pollTemps
{
	[NSObject cancelPreviousPerformRequestsWithTarget:self selector:@selector(pollTemps) object:nil];
	[self readTemps];
	[self performSelector:@selector(pollTemps) withObject:nil afterDelay:pollTime];
}

